import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Card, Button, Image, Container, Row, Col } from 'react-bootstrap';
//import all from reacticons/fa by *
import * as FaIcons from 'react-icons/fa';
import { FaStar } from "react-icons/fa";
import { FaRegStar } from "react-icons/fa";
import { FaHeart } from "react-icons/fa";
import { FaRegHeart } from "react-icons/fa";
import { FaShoppingCart } from "react-icons/fa";
import { FaRegShoppingCart } from "react-icons/fa";
import { FaArrowAltCircleRight } from "react-icons/fa";
import { FaArrowAltCircleLeft } from "react-icons/fa";
import { FaArrowAltCircleUp } from "react-icons/fa";
import { FaArrowAltCircleDown } from "react-icons/fa";

export default class SpecificMedicine extends Component {
    constructor(props) {
        super(props);
        this.state = {
            medicine: {},
            manufacturer: {}
        }
    }

    componentDidMount() {
        const id = this.props.match.params.id;
        axios.get(`http://localhost:5000/medicine/${id}`)
            .then(response => {
                this.setState({ medicine: response.data });
                axios.get(`http://localhost:5000/manufacturer/${response.data.manufacturer_id}`)
                    .then(response => {
                        this.setState({ manufacturer: response.data });
                    })
                    .catch(function (error) {
                        console.log(error);
                    })
            }
            )
            .catch(function (error) {
                console.log(error);
            })
    }

    render() {
        return (
            <div>
                <Container>
                    <Row>
                        <Col>
                            <Card style={{ width: '18rem' }}>
                                <Card.Img variant="top" src={this.state.medicine.image} />
                                <Card.Body>
                                    <Card.Title>{this.state.medicine.med_name}</Card.Title>
                                    <Card.Text>
                                        <p>Price: {this.state.medicine.price}</p>
                                        <p>Generic Name: {this.state.medicine.generic_name}</p>
                                        <p>Package Type: {this.state.medicine.package_type}</p>
                                        <p>Medicine Form: {this.state.medicine.med_form}</p>
                                        <p>Indication: {this.state.medicine.indication}</p>
                                        <p>Dosage: {this.state.medicine.dosage}</p>
                                        <p>Dosage Strength: {this.state.medicine.dosageStrength}</p>
                                        <p>Cautions: {this.state.medicine.cautions}</p>
                                    </Card.Text>
                                    <Link to={`/manufacturer/${this.state.medicine.manufacturer_id}`}>
                                        <Button variant="primary">Manufacturer: {this.state.manufacturer.manufacturer_name}</Button>
                                    </Link>
                                </Card.Body>
                            </Card>
                        </Col>
                    </Row>
                </Container>
            </div>
        )
    }
}

